import React, { Component } from "react";
import AppNav from "../AppNav";
import { Card, CardDeck } from "react-bootstrap";
class Products extends Component {
  state = {
    isLoading: true,
    Products: [],
  };

  async componentDidMount() {
    const response = await fetch("/products");
    const body = await response.json();
    this.setState({ isLoading: false, Products: body });
  }

  render() {
    const { isLoading, Products } = this.state;
    let productGroup = [];
    let allProductsGrouped = [];
    for (const [index, value] of Products.entries()) {
      productGroup.push(value);
      if ((index + 1) % 3 === 0) {
        allProductsGrouped.push(productGroup);
        productGroup = [];
      }
    }
    console.log(allProductsGrouped);
    return (
      <div>
        <AppNav />
        <div style={{ textAlign: "center" }} className="mb-4">
          <h1>Products</h1>
        </div>
        <div className="container">
          {allProductsGrouped.map((products) => (
            <div>
              <CardDeck>
                {products.map((product) => (
                  <Card key={product.id}>
                    <Card.Img variant="top" />
                    <Card.Body>
                      <Card.Title>{product.title}</Card.Title>
                      <Card.Text>{product.description}</Card.Text>
                    </Card.Body>
                    <Card.Footer>
                      <small className="text-muted">{product.price}$</small>
                    </Card.Footer>
                  </Card>
                ))}
              </CardDeck>
            </div>
          ))}
        </div>
        {/* {Products.map((product) => (
          <div key={product.id}>
            <h3>{product.title}</h3>
          </div>
        ))} */}
      </div>
    );
  }
}

export default Products;
