import React, { Component } from "react";
import AppNav from "../AppNav";
import { Row, Col, Dropdown, Select, Button, Card } from "react-bootstrap";
import "./Product.css";

class ProductPage extends Component {
  state = {
    Product: {},
    isLoading: true,
  };

  async componentDidMount() {
    const { id } = this.props.match.params;
    const response = await fetch(`/products/${id}`);
    const body = await response.json();
    this.setState({ Product: body, isLoading: false });
  }

  render() {
    const { Product, isLoading } = this.state;
    if (isLoading) {
      return <div>Loading...</div>;
    }
    return (
      <div>
        <AppNav />
        <div className="container mt-4" key={Product.id}>
          <Row>
            <Col className="col-xs-6">
              <img src="/images/cat.jpg" alt="" />
            </Col>
            <Col className="col-xs-6">
              <div>
                <h1 style={{ display: "block" }}>{Product.title}</h1>
                <h6>Reviews</h6>
                <p className="mt-4 mb-4">{Product.description}</p>
                <h2 style={{ display: "block" }}>${Product.price}</h2>
                <h6>Options</h6>
                <select className="dropdown mb-4">
                  <option value="size" disabled selected>
                    Size
                  </option>
                  <option value="a">XS</option>
                  <option value="a">S</option>
                  <option value="a">M</option>
                  <option value="a">L</option>
                </select>
                <Button variant="dark" className="addToCartBtn mt-4">
                  Add to Cart
                </Button>
                <Button className="ml-4" variant="dark">
                  -
                </Button>
                <p style={{ display: "inline" }} className="mx-3">
                  0
                </p>
                <Button variant="dark">+</Button>
              </div>
            </Col>
          </Row>
          <div className="mt-4">
            <Row>
              <Col>
                <h1 className="frequentlyBoughtTogether">
                  Frequently bought together
                </h1>
                <Card className="recommendedItem">
                  <Card.Img variant="top" src="holder.js/100px180" />
                  <Card.Body>
                    <Card.Title>Card Title</Card.Title>
                    <Card.Text>
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </Card.Text>
                    <Button variant="primary">Go somewhere</Button>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </div>
        </div>
      </div>
    );
  }
}

export default ProductPage;
