import React, { Component } from "react";
import { Form, Col, Button } from "react-bootstrap";
import AppNav from "../AppNav";
import axios from "axios";

class postProductForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      desc: "",
      stock: "",
      price: "",
      category: "",
      Categories: [],
    };
  }

  async componentDidMount() {
    const response = await fetch("/categories");
    const body = await response.json();
    this.setState({ Categories: body });
  }

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  changeHandlerSelect = (e) => {
    var element = document.getElementById("formCategory");
    this.setState({
      [e.target.name]: element.options[element.selectedIndex].value,
    });
  };

  submitHandler = (e) => {
    const data = {
      title: this.state.title,
      desc: this.state.desc,
      stock: this.state.stock,
      price: this.state.price,
      category: this.state.category,
    };
    e.preventDefault();
    console.log(this.state);
    // axios
    //   .post("http://localhost:8081/products", data)
    //   .then((response) => {
    //     console.log(response);
    //   })
    //   .catch((error) => {
    //     console.log(error);
    //   });
    axios({
      method: "post",
      url: "http://localhost:8081/products",
      data: data,
      headers: {},
    });
  };

  render() {
    const { title, desc, stock, price, category } = this.state;
    const { Categories } = this.state;
    return (
      <div>
        <AppNav />
        <div className="container mt-4">
          <Form onSubmit={this.submitHandler}>
            <Form.Row>
              <Form.Group as={Col} controlId="formProductName">
                <Form.Label>Product Name aaa</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter product name"
                  name="title"
                  value={title}
                  onChange={this.changeHandler}
                />
              </Form.Group>

              <Form.Group as={Col} controlId="formCategory">
                <Form.Label>Category</Form.Label>
                <Form.Control
                  as="select"
                  name="category"
                  onChange={this.changeHandler}
                >
                  <option value={category} disabled selected>
                    Alege o optiune
                  </option>
                  {/*<option value={category}>Bluze</option>
                  <option value={category}>T-shirt</option> */}
                  {Categories.map((category) => (
                    <option key={category.id}>{category.title}</option>
                  ))}
                </Form.Control>
              </Form.Group>
            </Form.Row>
            <Form.Group controlId="formDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows="7"
                placeholder="Description"
                name="desc"
                value={desc}
                onChange={this.changeHandler}
              />
            </Form.Group>
            {/* <Form.Group controlId="formGridAddress2">
                  <Form.Label>Address 2</Form.Label>
                  <Form.Control placeholder="Apartment, studio, or floor" />
                </Form.Group> */}
            <Form.Row>
              <Form.Group as={Col} controlId="formStock">
                <Form.Label>Stock</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="0"
                  name="stock"
                  value={stock}
                  onChange={this.changeHandler}
                />
              </Form.Group>

              {/* <Form.Group as={Col} controlId="formGridState">
                    <Form.Label>Stock</Form.Label>
                    <Form.Control as="select" value="Choose...">
                      <option>Choose...</option>
                      <option>...</option>
                    </Form.Control>
                  </Form.Group> */}

              <Form.Group as={Col} controlId="formPrice">
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="0"
                  name="price"
                  value={price}
                  onChange={this.changeHandler}
                />
              </Form.Group>
            </Form.Row>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </div>
      </div>
    );
  }
}

export default postProductForm;
